examples.query:
mutation {
  createTopic(
    input: {
      topic: {
        forumId: 1
        title: "My question relates to mutations..."
        body: "How do you write them?"
      }
    }
  ) {
    topic {
      nodeId
      id
      forumId
      title
      body
    }
  }
}

examples.result:
{
  "createTopic": {
    "topic": {
      "nodeId": "WyJ0b3BpY3MiLDRd",
      "id": 4,
      "forumId": 1,
      "title":
        "My question relates to mutations...",
      "body": "How do you write them?"
    }
  }
}

examples.query:
mutation {
  updateTopicById(
    input: {
      id: 1
      topicPatch: {
        title: "My (edited) title"
      }
    }
  ) {
    topic {
      nodeId
      id
      title
      body
    }
  }
}

# Works for a table like:
#
#   create table app_public.topics (
#     id serial primary key,
#     forum_id integer NOT NULL references app_public.forums on delete cascade,
#     title text NOT NULL,
#     body text DEFAULT ''::text NOT NULL
#   );

examples.result:
{
  "updateTopicById": {
    "topic": {
